
/* productivo */
SELECT 
  T0."UgpCode", 
  T1."ItemCode", 
  T1."ItemName", 
  T1."SalUnitMsr", 
  T1."NumInSale", 
  T1."InvntryUom",
  T1."U_SYP_PESOBRUTO", 
  T2."WhsCode", 
  T2."AvgPrice", 
  T2."OnHand", 
  (T2."AvgPrice" * T2."OnHand") AS "Costo_Stock"
FROM OUGP T0  
INNER JOIN OITM T1 ON T0."UgpEntry" = T1."UgpEntry" AND T1."ItemType" = 'I' ANd T1."validFor" = 'Y' 
INNER JOIN OITW T2 ON T1."ItemCode" = T2."ItemCode" --AND T2."WhsCode" = T1."DfltWH"
INNER JOIN OWHS T3 ON T2."WhsCode" = T3."WhsCode"
WHERE T1."ItemCode" LIKE '07%' --AND T2."OnHand" > 0
AND T2."AvgPrice" > 0
AND T2."WhsCode" IN ('10PTD', '10EPTD', '10FPTD', '10PTA', '10PTW') AND T2."Locked" = 'N'
--(T2."WhsCode" = T1."DfltWH" OR T2."WhsCode" LIKE '10%')
